select v.id id, e.empno empno, e.name empName, e.role_id roleId, r.name roleName, d.deptno deptno, d.name deptName, p.postno postno, p.name postName, v.reason reason, v.start_datetime start_datetime, v.end_datetime end_datetime, v.input_datetime input_datetime, v.state state, v.check_empno check_empno, v.check_datetime check_datetime from employee e, sys_role r, department d, post p, vacation v where e.role_id = r.role_id and e.deptno = d.deptno and e.postno = p.postno and v.empno = e.empno
and e.role_id = #{roleId}
and e.deptno = #{deptno}
and e.postno = #{postno}
and e.name like #{search} and e.empno like #{search}
limit #{pageNum}, #{pageSize}

